import React, { useState, useEffect } from 'react';
import DeviceCard from './components/DeviceCard';
import SystemStatus from './components/SystemStatus';
import VoiceControl from './components/VoiceControl';
import ApiService from './services/api';
import './App.css';

function App() {
  const [devices, setDevices] = useState([]);
  const [systemStatus, setSystemStatus] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Load d·ªØ li·ªáu khi component mount
  useEffect(() => {
    loadDevices();
    loadSystemStatus();

    // Auto refresh m·ªói 5 gi√¢y
    const interval = setInterval(() => {
      loadDevices();
      loadSystemStatus();
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  // L·∫•y danh s√°ch thi·∫øt b·ªã
  const loadDevices = async () => {
    try {
      const response = await ApiService.getDevices();
      if (response.success) {
        setDevices(response.data);
      }
      setError(null);
    } catch (err) {
      setError('Kh√¥ng th·ªÉ k·∫øt n·ªëi ƒë·∫øn server');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // L·∫•y tr·∫°ng th√°i h·ªá th·ªëng
  const loadSystemStatus = async () => {
    try {
      const response = await ApiService.getSystemStatus();
      if (response.success) {
        setSystemStatus(response.data);
      }
    } catch (err) {
      console.error('Error loading system status:', err);
    }
  };

  // ƒêi·ªÅu khi·ªÉn LED
  const handleLEDToggle = async (deviceName, state) => {
    try {
      const response = await ApiService.controlLED(state);
      if (response.success) {
        loadDevices(); // Reload ƒë·ªÉ c·∫≠p nh·∫≠t tr·∫°ng th√°i
      }
    } catch (err) {
      alert('L·ªói khi ƒëi·ªÅu khi·ªÉn LED');
      console.error(err);
    }
  };

  // ƒêi·ªÅu khi·ªÉn qu·∫°t
  const handleFanToggle = async (deviceName, state) => {
    try {
      const response = await ApiService.controlFan(state);
      if (response.success) {
        loadDevices();
      }
    } catch (err) {
      alert('L·ªói khi ƒëi·ªÅu khi·ªÉn qu·∫°t');
      console.error(err);
    }
  };

  // X·ª≠ l√Ω toggle chung
  const handleDeviceToggle = (deviceName, state) => {
    if (deviceName === 'LED') {
      handleLEDToggle(deviceName, state);
    } else if (deviceName === 'Fan') {
      handleFanToggle(deviceName, state);
    }
  };

  // X·ª≠ l√Ω l·ªánh voice
  const handleVoiceCommand = async (device, action) => {
    try {
      if (device === 'led') {
        await handleLEDToggle('LED', action);
      } else if (device === 'fan') {
        await handleFanToggle('Fan', action);
      } else if (device === 'all') {
        const response = await ApiService.controlAll(action);
        if (response.success) {
          loadDevices(); // Reload ƒë·ªÉ c·∫≠p nh·∫≠t tr·∫°ng th√°i
        }
      }
    } catch (err) {
      alert('L·ªói khi ƒëi·ªÅu khi·ªÉn thi·∫øt b·ªã');
      console.error(err);
    }
  };

  if (loading) {
    return (
      <div className="app-container">
        <div className="loading">
          <div className="spinner"></div>
          <p>ƒêang t·∫£i...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>üè† Smart Home Dashboard</h1>
        <p>ƒêi·ªÅu khi·ªÉn thi·∫øt b·ªã IoT v·ªõi ESP32</p>
      </header>

      {error && (
        <div className="error-banner">
          ‚ö†Ô∏è {error}
        </div>
      )}

      <div className="main-content">
        {/* Ph·∫ßn tr·∫°ng th√°i h·ªá th·ªëng */}
        <section className="status-section">
          <SystemStatus status={systemStatus} loading={!systemStatus} />
        </section>

        {/* Ph·∫ßn ƒëi·ªÅu khi·ªÉn thi·∫øt b·ªã */}
        <section className="devices-section">
          <h2>üì± Thi·∫øt b·ªã</h2>
          <div className="devices-grid">
            {devices.map((device) => (
              <DeviceCard
                key={device.id}
                device={device}
                onToggle={handleDeviceToggle}
                onSpeedChange={device.type === 'fan' ? handleFanSpeedChange : null}
              />
            ))}
          </div>
        </section>

        {/* Ph·∫ßn ƒëi·ªÅu khi·ªÉn gi·ªçng n√≥i */}
        <section className="voice-section">
          <VoiceControl onCommand={handleVoiceCommand} />
        </section>
      </div>

      <footer className="app-footer">
        <p>¬© 2025 Smart Home Project</p>
        <p>Create By Zuy</p>
      </footer>
    </div>
  );
}

export default App;
