import React, { useState, useEffect } from 'react';
import DeviceCard from './components/DeviceCard';
import SystemStatus from './components/SystemStatus';
import VoiceControl from './components/VoiceControl';
import ApiService from './services/api';
import './App.css';

function App() {
  const [devices, setDevices] = useState([]);
  const [systemStatus, setSystemStatus] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Load dữ liệu khi component mount
  useEffect(() => {
    loadDevices();
    loadSystemStatus();

    // Auto refresh mỗi 5 giây
    const interval = setInterval(() => {
      loadDevices();
      loadSystemStatus();
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  // Lấy danh sách thiết bị
  const loadDevices = async () => {
    try {
      const response = await ApiService.getDevices();
      if (response.success) {
        setDevices(response.data);
      }
      setError(null);
    } catch (err) {
      setError('Không thể kết nối đến server');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // Lấy trạng thái hệ thống
  const loadSystemStatus = async () => {
    try {
      const response = await ApiService.getSystemStatus();
      if (response.success) {
        setSystemStatus(response.data);
      }
    } catch (err) {
      console.error('Error loading system status:', err);
    }
  };

  // Điều khiển LED
  const handleLEDToggle = async (deviceName, state) => {
    try {
      const response = await ApiService.controlLED(state);
      if (response.success) {
        loadDevices(); // Reload để cập nhật trạng thái
      }
    } catch (err) {
      alert('Lỗi khi điều khiển LED');
      console.error(err);
    }
  };

  // Điều khiển quạt
  const handleFanToggle = async (deviceName, state) => {
    try {
      const fan = devices.find(d => d.name === 'Fan');
      const speed = state === 'on' ? (fan?.speed || 255) : 0;
      const response = await ApiService.controlFan(state, speed);
      if (response.success) {
        loadDevices();
      }
    } catch (err) {
      alert('Lỗi khi điều khiển quạt');
      console.error(err);
    }
  };

  // Thay đổi tốc độ quạt
  const handleFanSpeedChange = async (speed) => {
    try {
      await ApiService.controlFan('on', speed);
      loadDevices();
    } catch (err) {
      console.error('Error changing fan speed:', err);
    }
  };

  // Xử lý toggle chung
  const handleDeviceToggle = (deviceName, state) => {
    if (deviceName === 'LED') {
      handleLEDToggle(deviceName, state);
    } else if (deviceName === 'Fan') {
      handleFanToggle(deviceName, state);
    }
  };

  // Xử lý lệnh voice
  const handleVoiceCommand = (device, action) => {
    if (device === 'led') {
      handleLEDToggle('LED', action);
    } else if (device === 'fan') {
      handleFanToggle('Fan', action);
    } else if (device === 'all') {
      handleLEDToggle('LED', action);
      handleFanToggle('Fan', action);
    }
  };

  if (loading) {
    return (
      <div className="app-container">
        <div className="loading">
          <div className="spinner"></div>
          <p>Đang tải...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>🏠 Smart Home Dashboard</h1>
        <p>Điều khiển thiết bị IoT với ESP32</p>
      </header>

      {error && (
        <div className="error-banner">
          ⚠️ {error}
        </div>
      )}

      <div className="main-content">
        {/* Phần trạng thái hệ thống */}
        <section className="status-section">
          <SystemStatus status={systemStatus} loading={!systemStatus} />
        </section>

        {/* Phần điều khiển thiết bị */}
        <section className="devices-section">
          <h2>📱 Thiết bị</h2>
          <div className="devices-grid">
            {devices.map((device) => (
              <DeviceCard
                key={device.id}
                device={device}
                onToggle={handleDeviceToggle}
                onSpeedChange={device.type === 'fan' ? handleFanSpeedChange : null}
              />
            ))}
          </div>
        </section>

        {/* Phần điều khiển giọng nói */}
        <section className="voice-section">
          <VoiceControl onCommand={handleVoiceCommand} />
        </section>
      </div>

      <footer className="app-footer">
        <p>© 2025 Smart Home Project - IoT với ESP32</p>
        <p>TDM University - Year 4, Semester 1</p>
      </footer>
    </div>
  );
}

export default App;
