// src/components/SystemStatus.jsx - Hi·ªÉn th·ªã tr·∫°ng th√°i k·∫øt n·ªëi
import React from 'react';

function SystemStatus({ status, loading }) {
  if (loading) {
    return (
      <div className="system-status">
        <h3>üìä Tr·∫°ng th√°i h·ªá th·ªëng</h3>
        <p>ƒêang t·∫£i...</p>
      </div>
    );
  }

  const getStatusIcon = (state) => {
    if (state === 'connected' || state === 'running') return 'üü¢';
    if (state === 'disconnected') return 'üî¥';
    return 'üü°';
  };

  return (
    <div className="system-status">
      <h3>üìä Tr·∫°ng th√°i h·ªá th·ªëng</h3>
      <div className="status-grid">
        <div className="status-item">
          <span className="status-icon">{getStatusIcon(status?.server)}</span>
          <span className="status-label">Server</span>
          <span className="status-value">{status?.server || 'Unknown'}</span>
        </div>

        <div className="status-item">
          <span className="status-icon">{getStatusIcon(status?.database)}</span>
          <span className="status-label">PostgreSQL</span>
          <span className="status-value">{status?.database || 'Unknown'}</span>
        </div>

        <div className="status-item">
          <span className="status-icon">{getStatusIcon(status?.mqtt)}</span>
          <span className="status-label">MQTT Broker</span>
          <span className="status-value">{status?.mqtt || 'Unknown'}</span>
        </div>

        <div className="status-item">
          <span className="status-icon">üîå</span>
          <span className="status-label">ESP32</span>
          <span className="status-value">
            {status?.mqtt === 'connected' ? 'Connected' : 'Checking...'}
          </span>
        </div>
      </div>

      <div className="status-time">
        C·∫≠p nh·∫≠t: {status?.timestamp || 'N/A'}
      </div>
    </div>
  );
}

export default SystemStatus;
