// src/components/VoiceControl.jsx - Điều khiển bằng giọng nói (Google Assistant)
import React, { useState, useEffect } from 'react';

function VoiceControl({ onCommand }) {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [recognition, setRecognition] = useState(null);

  useEffect(() => {
    // Kiểm tra trình duyệt có hỗ trợ Web Speech API không
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      const recognitionInstance = new SpeechRecognition();
      
      recognitionInstance.continuous = false;
      recognitionInstance.interimResults = false;
      recognitionInstance.lang = 'vi-VN';

      recognitionInstance.onresult = (event) => {
        const speechResult = event.results[0][0].transcript;
        setTranscript(speechResult);
        handleVoiceCommand(speechResult);
      };

      recognitionInstance.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setIsListening(false);
      };

      recognitionInstance.onend = () => {
        setIsListening(false);
      };

      setRecognition(recognitionInstance);
    }
  }, []);

  const handleVoiceCommand = (command) => {
    const lowerCommand = command.toLowerCase();
    
    // Phân tích lệnh voice
    if (lowerCommand.includes('bật đèn') || lowerCommand.includes('mở đèn')) {
      onCommand('led', 'on');
    } else if (lowerCommand.includes('tắt đèn')) {
      onCommand('led', 'off');
    } else if (lowerCommand.includes('bật quạt') || lowerCommand.includes('mở quạt')) {
      onCommand('fan', 'on');
    } else if (lowerCommand.includes('tắt quạt')) {
      onCommand('fan', 'off');
    } else if (lowerCommand.includes('bật tất cả')) {
      onCommand('all', 'on');
    } else if (lowerCommand.includes('tắt tất cả')) {
      onCommand('all', 'off');
    }
  };

  const startListening = () => {
    if (recognition) {
      setTranscript('');
      setIsListening(true);
      recognition.start();
    } else {
      alert('Trình duyệt của bạn không hỗ trợ nhận dạng giọng nói');
    }
  };

  const stopListening = () => {
    if (recognition) {
      recognition.stop();
      setIsListening(false);
    }
  };

  return (
    <div className="voice-control">
      <h3>🎤 Điều khiển bằng giọng nói</h3>
      
      <div className="voice-button-container">
        <button
          className={`btn-voice ${isListening ? 'listening' : ''}`}
          onClick={isListening ? stopListening : startListening}
        >
          {isListening ? (
            <>
              <span className="pulse-ring"></span>
              🎤 Đang nghe...
            </>
          ) : (
            <>🎤 Nhấn để nói</>
          )}
        </button>
      </div>

      {transcript && (
        <div className="voice-transcript">
          <p><strong>Bạn nói:</strong> {transcript}</p>
        </div>
      )}

      <div className="voice-commands-help">
        <details>
          <summary>📝 Các lệnh có thể dùng</summary>
          <ul>
            <li>"Bật đèn" / "Mở đèn"</li>
            <li>"Tắt đèn"</li>
            <li>"Bật quạt" / "Mở quạt"</li>
            <li>"Tắt quạt"</li>
            <li>"Bật tất cả"</li>
            <li>"Tắt tất cả"</li>
          </ul>
        </details>
      </div>

      <div className="google-assistant-note">
        <p>💡 <strong>Tip:</strong> Bạn cũng có thể dùng Google Assistant trên điện thoại:</p>
        <p>"Hey Google, talk to SmartHomeController"</p>
      </div>
    </div>
  );
}

export default VoiceControl;
