// src/components/DeviceCard.jsx - Component hiá»ƒn thá»‹ thiáº¿t bá»‹ LED/Fan
import React from 'react';

function DeviceCard({ device, onToggle, onSpeedChange }) {
  const isOn = device.state === 'on';
  const isFan = device.type === 'fan';

  return (
    <div className="device-card">
      <div className="device-header">
        <h2>{device.name}</h2>
        <span className={`status-badge ${isOn ? 'on' : 'off'}`}>
          {isOn ? 'ðŸŸ¢ Báº¬T' : 'ðŸ”´ Táº®T'}
        </span>
      </div>

      <div className="device-icon">
        {isFan ? 'ðŸŒ€' : 'ðŸ’¡'}
      </div>

      <div className="device-controls">
        <button
          className={`btn-toggle ${isOn ? 'btn-on' : 'btn-off'}`}
          onClick={() => onToggle(device.name, isOn ? 'off' : 'on')}
        >
          {isOn ? 'ðŸ”´ Táº®T' : 'ðŸŸ¢ Báº¬T'}
        </button>

        {isFan && (
          <div className="fan-speed-control">
            <label>Tá»‘c Ä‘á»™: {device.speed || 0}</label>
            <input
              type="range"
              min="0"
              max="255"
              value={device.speed || 0}
              onChange={(e) => onSpeedChange(parseInt(e.target.value))}
              disabled={!isOn}
              className="speed-slider"
            />
            <div className="speed-labels">
              <span>0</span>
              <span>128</span>
              <span>255</span>
            </div>
          </div>
        )}
      </div>

      <div className="device-info">
        <p>Loáº¡i: {device.type}</p>
        <p>Cáº­p nháº­t: {new Date(device.last_updated).toLocaleString('vi-VN')}</p>
      </div>
    </div>
  );
}

export default DeviceCard;
